import React, { Component } from 'react';
import { Grid } from 'react-bootstrap';
import { Row } from 'react-bootstrap';
import { Col } from 'react-bootstrap';
import { Link, BrowserRouter, Route } from 'react-router-dom';
import { NavDropdown } from 'react-bootstrap';
import { MenuItem } from 'react-bootstrap';
import './App.css';
import * as actions from '../actions';
import { connect } from 'react-redux';
import Carousel from './carousel';
import Head from "react-helmet";


class Table extends React.Component {
    
    render() {
    const row1 = [];
    const row2 = [];
    const row3 = [];
    const row4 = [];

    var num = 0;
    this.props.items.forEach((item) => {
        if(num % 4 == 0){
            row1.push(<Item item={item} key={item._id}/>);
        }
        else if(num % 4 == 1) {
            row2.push(<Item item={item} key={item._id}/>);
        }
        else if(num % 4 == 2){
            row3.push(<Item item={item} key={item._id}/>);
        }
        else if(num % 4 == 3){
            row4.push(<Item item={item} key={item._id}/>);
        }
        num++;
    });
      return (
        <div>
            <Grid>
                <Row>
                    <Col xs={6} md={3}>
                        {row1}
                    </Col>
                    <Col xs={6} md={3}>
                        {row2}
                    </Col>
                    <Col xs={6} md={3}>
                        {row3}
                    </Col>
                    <Col xs={6} md={3}>
                        {row4}
                    </Col>
                </Row>
            </Grid>
        </div>
      );
    }
  }

function handleErrors(response) {
    if (!response.ok) {
        throw Error(response.statusText);
    }
    return response;
}

class Item extends React.Component {
    render() {
        const item = this.props.item;
    
        return (
        <div>
            <img src={item.imgUrl}></img>
            <p><Link to={{ pathname: '/items/'+item._id, state: { _id: item._id} }}>{item.name}</Link></p>
            <p>${item.price}</p>
        </div>
        );
      }
}

class landing extends React.Component {
    
    componentDidMount() {
        this.props.fetchItems();
    }

    render() {
        return(    
        
        <div>   
            <Carousel/>
            <div className="Landing">
            <header className="App-header2">
					<h2>WHAT'S TRENDING</h2>	
			</header> 
                <Table
                    items={this.props.items}
                />
            </div>

            


        </div>
        );
    }
}

const mapStateToProps = (state) => {
	return {
        items: state.itemReducer,
    }
    
}

export default connect(mapStateToProps, actions)(landing);
